import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  createHttpLink,
} from "@apollo/client";
import { setContext } from "@apollo/client/link/context";

import "./App.css";
import Header from "./components/Header";
import About from "./components/About";
import Party from "./components/Party";
import User from "./components/User";
import Live from "./components/Live";
import Footer from "./components/Footer";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import NewParty from "./components/NewParty";
import Store, { Context } from "./utils/GlobalState";
import RuleForm from "./components/RuleForm";

const httpLink = createHttpLink({
  uri: "/graphql",
});

const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem("id_token");
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    },
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache(),
});

//import LoggingIn from "./LoggingIn";
function App() {
  return (
    <ApolloProvider client={client}>
      <Router>
        <Store>
          <Header className="navBarContainer" />
          {(Context.party = "new" ? <NewParty /> : <Party />)}
          {(Context.party = "new" ? <RuleForm /> : <Party />)}
          <User className="userContainer" />
          <Party className="partyContainer" />
          <About className="aboutContainer" />
          <Live className="liveContainer" />
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </Routes>
          <Footer class="footerContainer" />
        </Store>
      </Router>
    </ApolloProvider>
  );
}

export default App;
