import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  createHttpLink,
} from "@apollo/client";
import { setContext } from "@apollo/client/link/context";

import "./App.css";
import Header from "./components/Header";
import About from "./components/About";
import LiveParty from "./pages/LiveParty";
import User from "./components/User";
import Live from "./components/Live";
import Footer from "./components/Footer";
import Login from "./pages/Login";

import Signup from "./pages/Signup";
import Partypage from "./pages/Partypage";
import Home from "./pages/Home";
import Store, { Context } from "./utils/GlobalState";
//import RuleForm from "./components/RuleForm";
//import logo from "../src/images/party_foul.png";

const httpLink = createHttpLink({
  uri: "/graphql",
});

const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem("id_token");
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    },
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache(),
});

//import LoggingIn from "./LoggingIn";
function App() {
  return (
    <ApolloProvider client={client}>
      <Router>
        <Store>
          <div className="App">
            <Header className="navBarContainer" />

            <div className="task-manager">
              <div className="left-bar">
                <div className="left-content">
                  <User className="userContainer" />
                </div>
              </div>
              <div>
                <About className="aboutContainer" />
              </div>
              <div className="right-bar">
                <div className="top-part">
                  <Live className="liveContainer" />
                </div>
              </div>
            </div>
            <Footer class="footerContainer" />
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/" element={<Home />} />
              <Route path="/liveparty" element={<LiveParty />} />
              <Route path="/party" element={<Partypage />} />
            </Routes>
          </div>
        </Store>
      </Router>
    </ApolloProvider>
  );
}

export default App;
